mapper.select:
SELECT  COUNT(*)
  FROM  LIKET
 WHERE  LIKE_CATEGORY = #{likeCategory}
		AND LIKE_BOARDID = #{likeBoardid}
		AND USER_ID = #{userId}

mapper.delete:
DELETE  FROM  LIKET
 		WHERE LIKE_CATEGORY = #{likeCategory}
			  AND LIKE_BOARDID = #{likeBoardid}
			  AND USER_ID = #{userId}
