mapper.insert:
SELECT NVL(MAX(NOTICE_ID),0)+1 FROM NOTICET
INSERT INTO NOTICET ( NOTICE_ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_HIT, NOTICE_DATE, USER_ID
, NOTICE_IMG
) VALUES ( #{noticeId}, #{noticeTitle}, #{noticeContent}, 0, sysdate, 'admin'
, #{noticeImg}
)

mapper.update:
UPDATE NOTICET SET NOTICE_TITLE = #{noticeTitle},
				   NOTICE_CONTENT = #{noticeContent}
	
				 , NOTICE_IMG = #{noticeImg}
	
			WHERE  NOTICE_ID = #{noticeId}
