mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT TRAVEL_NO, TRAVEL_PERSON, TRAVEL_PAY, TRAVEL_SCHE, TRAVEL_START, TRAVEL_END, TRAVEL_TITLE, TRAVEL_CONTENT, TRAVEL_DATE, TRAVEL_HIT, TRAVEL_LIKE, TINFO_ID, USER_ID, TRAVEL_WITH, TRAVEL_PIC FROM TRAVELBOARDT
tinfo_id = #{searchTinfo}
ORDER BY ${sortCol}
ORDER BY TRAVEL_NO DESC
) A ) B WHERE RN BETWEEN #{first} AND #{last}

mapper.update:
UPDATE TRAVELBOARDT SET TRAVEL_PERSON = #{travelPerson}, TRAVEL_PAY = #{travelPay}, TRAVEL_SCHE = #{travelSche}, TRAVEL_START = TO_DATE(#{travelStart}, 'yyyy-mm-dd hh24:mi:ss'), TRAVEL_END = TO_DATE(#{travelEnd}, 'yyyy-mm-dd hh24:mi:ss'), TRAVEL_TITLE = #{travelTitle}, TRAVEL_CONTENT = #{travelContent}, TRAVEL_WITH = #{travelWith}
,TRAVEL_PIC = #{travelPic}
WHERE TRAVEL_NO = #{travelNo}

mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM RN FROM (
		SELECT * FROM(
		SELECT * FROM TRAVELBOARDT
		ORDER BY TRAVEL_LIKE DESC)
		WHERE ROWNUM <= 120
		) A ) B WHERE RN BETWEEN #{first} AND #{last}
